import React, { useState, useEffect } from 'react';

function App() {
  const [liters, setLiters] = useState(null);
  const [orderedLiters, setOrderedLiters] = useState(0);
  const [warning, setWarning] = useState(false);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch('http://api.example.com/liters');
      const data = await response.json();
      setLiters(data.liters);
    }
    fetchData();
  }, []);

  useEffect(() => {
    if (liters !== null && liters - orderedLiters <= 50) {
      setWarning(true);
    } else {
      setWarning(false);
    }
  }, [liters, orderedLiters]);

  const handleOrder = (amount) => {
    setOrderedLiters(orderedLiters + amount);
  };

  return (
    <div>
      <h1>Current liters: {liters !== null ? liters : 'Loading...'}</h1>
      <h2>Liters ordered: {orderedLiters}</h2>
      {warning && <p>Warning: Only {liters - orderedLiters} liters left!</p>}
      <button onClick={() => handleOrder(10)}>Order 10 liters</button>
      <button onClick={() => handleOrder(20)}>Order 20 liters</button>
    </div>
  );
}

export default App;
