import React, { useState, useEffect } from 'react';
// import axios from 'axios';

function InventoryPage() {
  const [inventoryItems, setInventoryItems] = useState([]);
  const [newItemName, setNewItemName] = useState('');
  const [newItemQuantity, setNewItemQuantity] = useState('');

  // Fetch the inventory items from the backend API
  useEffect(() => {
    axios.get('/api/inventory')
      .then(response => setInventoryItems(response.data));
  }, []);

  // Add a new inventory item
  function addInventoryItem(event) {
    event.preventDefault();
    const newItem = { name: newItemName, quantity: newItemQuantity };
    axios.post('/api/inventory', newItem)
      .then(response => {
        setInventoryItems([...inventoryItems, response.data]);
        setNewItemName('');
        setNewItemQuantity('');
      });
  }

  // Render the inventory items as a table
  return (
    <div>
      <h1>Inventory</h1>
      <form onSubmit={addInventoryItem}>
        <input type="text" placeholder="Item name" value={newItemName} onChange={event => setNewItemName(event.target.value)} />
        <input type="number" placeholder="Quantity" value={newItemQuantity} onChange={event => setNewItemQuantity(event.target.value)} />
        <button type="submit">Add item</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {inventoryItems.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default InventoryPage;
